import React, { useState } from 'react';
import axios from 'axios';
import { PaperAirplaneIcon, DocumentIcon } from '@heroicons/react/24/solid';

interface Message {
  role: 'user' | 'assistant';
  content: string;
}

interface TableOfContents {
  chapter: number;
  title: string;
  page: number;
}

const hardcodedTOC: TableOfContents[] = [
  { chapter: 1, title: "Relativity I", page: 1 },
  { chapter: 2, title: "Relativity II", page: 41 },
  { chapter: 3, title: "The Quantum Theory of Light", page: 65 },
  { chapter: 4, title: "The Particle Nature of Matter", page: 106 },
  { chapter: 5, title: "Matter Waves", page: 151 },
  { chapter: 6, title: "Quantum Mechanics in One Dimension", page: 191 },
  { chapter: 7, title: "Tunneling Phenomena", page: 231 },
  { chapter: 8, title: "Quantum Mechanics in Three Dimensions", page: 260 },
  { chapter: 9, title: "Atomic Structure", page: 295 },
  { chapter: 10, title: "Statistical Physics", page: 334 },
  { chapter: 11, title: "Molecular Structure", page: 372 },
  { chapter: 12, title: "The Solid State", page: 404 },
  { chapter: 13, title: "Nuclear Structure", page: 463 },
  { chapter: 14, title: "Nuclear Physics Applications", page: 503 },
  { chapter: 15, title: "Elementary Particles", page: 547 },
  { chapter: 16, title: "Cosmology (Web Only)", page: 0 },
];

const appendices = [
  "Appendix A Best Known Values for Physical Constants A.1",
  "Appendix B Table of Selected Atomic Masses A.2",
  "Appendix C Nobel Prizes A.7",
  "Answers to Odd-Numbered Problems A.12"
];

function App() {
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [showTOC, setShowTOC] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMessage: Message = { role: 'user', content: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setLoading(true);
    setError(null);

    try {
      const response = await axios.post(
        'https://api.meta.ai/v1/llama',
        {
          prompt: input,
          max_tokens: 1000,
          temperature: 0.7,
        },
        {
          headers: {
            'Authorization': `Bearer ${process.env.REACT_APP_LLAMA_API_KEY}`,
            'Content-Type': 'application/json',
          },
        }
      );

      if (response.data && response.data.response) {
        const assistantMessage: Message = {
          role: 'assistant',
          content: response.data.response,
        };
        setMessages(prev => [...prev, assistantMessage]);
      } else {
        throw new Error('Invalid response format from API');
      }
    } catch (error) {
      console.error('Error:', error);
      if (axios.isAxiosError(error)) {
        if (error.response?.status === 401) {
          setError('Authentication failed. Please check your API key.');
        } else if (error.response?.status === 429) {
          setError('Rate limit exceeded. Please try again later.');
        } else {
          setError(`API Error: ${error.response?.data?.message || error.message}`);
        }
      } else {
        setError('An unexpected error occurred. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files[0]) {
      const file = event.target.files[0];
      if (file.type === 'application/pdf') {
        setSelectedFile(file);
        setShowTOC(true);
      } else {
        alert('Please upload a PDF file');
      }
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
      <div className="relative py-3 sm:max-w-4xl sm:mx-auto">
        <div className="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
          <div className="max-w-4xl mx-auto">
            <div className="divide-y divide-gray-200">
              <div className="py-8 text-base leading-6 space-y-4 text-gray-700 sm:text-lg sm:leading-7">
                <h1 className="text-3xl font-bold text-center mb-8 text-gray-900">Physics Textbook TOC</h1>

                {/* File Upload */}
                <div className="mb-8">
                  <label className="flex flex-col items-center justify-center w-full h-32 border-2 border-gray-300 border-dashed rounded-lg cursor-pointer bg-gray-50 hover:bg-gray-100">
                    <div className="flex flex-col items-center justify-center pt-5 pb-6">
                      <DocumentIcon className="w-10 h-10 mb-3 text-gray-400" />
                      <p className="mb-2 text-sm text-gray-500">
                        <span className="font-semibold">Click to upload</span> or drag and drop
                      </p>
                      <p className="text-xs text-gray-500">PDF files only</p>
                    </div>
                    <input
                      type="file"
                      className="hidden"
                      accept=".pdf"
                      onChange={handleFileChange}
                    />
                  </label>
                </div>

                {/* Table of Contents */}
                {showTOC && (
                  <div className="mt-8">
                    <h2 className="text-2xl font-bold mb-4">Table of Contents</h2>
                    <div className="space-y-2">
                      {hardcodedTOC.map((item) => (
                        <div key={item.chapter} className="flex items-baseline">
                          <span className="w-12 font-semibold">Chapter {item.chapter}</span>
                          <span className="flex-grow">{item.title}</span>
                          {item.page > 0 && <span className="ml-4 text-gray-500">{item.page}</span>}
                        </div>
                      ))}
                    </div>

                    {/* Appendices */}
                    <div className="mt-8">
                      <h3 className="text-xl font-bold mb-4">Appendices</h3>
                      <div className="space-y-2">
                        {appendices.map((appendix, index) => (
                          <div key={index} className="text-gray-700">
                            {appendix}
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
